mapper.update:
UPDATE note_follows

    status = #{noteFollow.status},
    pet_id = #{noteFollow.petId}

WHERE user_id = #{noteFollow.userId} AND note_id = #{noteFollow.noteId}

UPDATE note_follows

    pet_id = null,
    status = #{status}

WHERE note_id = #{noteId} and user_id != #{userId}

UPDATE note_follows

    pet_id = null,
    status = #{status}

WHERE note_id = #{noteId}

UPDATE note_follows

    status = #{updateStatus}

WHERE status=#{status} and note_id=#{noteId}

mapper.select:
SELECT

FROM note_follows
WHERE user_id = #{userId} and note_id =#{noteId}
